mapper.select:
select * from board_info

        user_id = #{user_id} 
        and 
        board_title = #{board_title} 
        and 
        board_created_at = #{board_created_at}

mapper.update:
update board_info

        board_category = #{board_category}, 
        board_title = #{board_title}, 
        board_content = #{board_content}, 
        board_updated_at = #{board_updated_at} 


        board_no = #{board_no}
