mapper.select:
select * from community_post 

		post_subject = #{post_subject} 
		and 
		user_id = #{user_id} 
		and 
		post_created_at = #{post_created_at}

mapper.update:
update community_post
type_id = #{type_id}, theme_id = #{theme_id}, post_subject = #{post_subject}, post_content = #{post_content}, post_images = #{post_images}, post_files = #{post_files}, post_updated_at = #{post_updated_at},
post_id = #{post_id}

update community_post set 
post_cmtcnt = (select count(cmt_id) as post_cmtcnt from cunsult_comment where post_id = #{post_id} group by post_id) 
		where post_id = #{post_id}
